import React, { useState, useEffect } from 'react'
import { Link } from "react-router-dom";
import logo from '../assets/images/logo.png'
import styles from '../assets/cssModules/navbar.module.css'

const Navbar = () => {

    const [navClass, setNavClass] = useState('navbar navbar-expand-lg navbar-light');
    
    useEffect(() => {
        switch (window.location.pathname) {
            case '/':
            case '/ourteam':
                setNavClass((prevNavClass) => `navbar navbar-expand-lg navbar-light ${styles.transNav}`);
                break;
            case '/contact':
            case '/works':
            case '/industries':
            case '/whychooseus':
                setNavClass((prevNavClass) => `navbar navbar-expand-lg navbar-light bg-white ${styles.whiteNav}`);
                break;
            default:
                setNavClass((prevNavClass) => 'navbar navbar-expand-lg navbar-light');
        }
    }, []);

    // switch(props.navTheme) {
    //     case 'White':
    //         Nav = 'navbar navbar-expand-lg navbar-light bg-white' + styles.whiteNav;
    //         break;
    //     case 'Trans':
    //         Nav = 'navbar navbar-expand-lg navbar-light' + styles.transNav;
    //         break;
    //     default:
    //         Nav = 'navbar navbar-expand-lg navbar-light';
    // }
    // console.log(Nav);
    return (
        <nav className={navClass}>
            <Link to="/" className="navbar-brand pt-3 pb-1">
                {/* Logo Image */}
                <img src={logo} alt="logo" className={styles.logo} />
            </Link>
            <button
                className="navbar-toggler"
                type="button"
                data-toggle="collapse"
                data-target="#navbarContent"
                aria-controls="navbarContent"
                aria-expanded="false"
                aria-label="Toggle navigation"
            >
                <span className="navbar-toggler-icon" />
            </button>
            <div id="navbarContent" className="collapse navbar-collapse">
                <ul className="navbar-nav ml-auto">
                    <li className="nav-item align-self-lg-center text-lg-center mx-lg-3">
                        <Link to="/whychooseus" className="nav-link">
                            WHY CHOOSE US
                        </Link>
                    </li>
                    <li className="nav-item align-self-lg-center text-lg-center mx-lg-3">
                        <Link to="/industries" className="nav-link">
                            INDUSTRIES
                        </Link>
                    </li>
                    <li className="nav-item align-self-lg-center text-lg-center mx-lg-3">
                        <Link to="/works" className="nav-link">
                            WORKS
                        </Link>
                    </li>
                    <li className="nav-item align-self-lg-center text-lg-center mx-lg-3">
                        <Link to="/ourteam" className="nav-link">
                            OUR TEAM
                        </Link>
                    </li>
                    <li className="nav-item align-self-lg-center text-lg-center mx-lg-3">
                        <Link to="/contact" className="nav-link">
                            CONTACT
                        </Link>
                    </li>
                    <li className="nav-item align-self-lg-center text-lg-center ml-lg-3">
                        <a href="/#" className="nav-link">
                            ENG/VN
                        </a>
                    </li>
                </ul>
            </div>
        </nav>

    )
}

export default Navbar;
