import React, { useEffect, useState } from 'react'
import styles from '../../../assets/cssModules/homepage.module.css';
import useAxiosFetch from '../../../hooks/useAxiosFetch';
import defaultData from '../../../data/defaultData.json'

const Services = () => {

    const [serviceList, setServiceList] = useState(defaultData.services.map((service, index) => {
        return (
            <div className={`col-sm-6 col-md-4 px-lg-${index % 3 === 2 ? 4 : 5} 
                px-xl-${(index + 1) % 3 === 2 ? 4 : 5} mb-5 service`} key={service.id}>
                <div className="pic">
                    <img
                        src={service.imgURL}
                        alt="sv"
                        className="img-fluid"
                    />
                </div>
                <h4 className={styles.h4}>{service.svName}</h4>
                <p>{service.content}</p>
            </div>
        )
    }));

    const { data, fetchError, isLoading} = useAxiosFetch('http://localhost:3500/services/');

    useEffect(() => {
        if (fetchError != null) {
            alert(fetchError);
        }
    }, [fetchError]);

    useEffect(() => {
        if (isLoading) return;

    }, [isLoading])

    return (
        <section id="services">
            <div className="container">
                <div className="row">
                    <div className="col-12">
                        <h2 className={styles.h2}>OUR SERVICES</h2>
                    </div>
                    {serviceList}
                </div>
            </div>
        </section>
    )
}

export default Services
