import React, { useEffect, useState } from 'react'
import styles from '../../../assets/cssModules/homepage.module.css';
import useAxiosFetch from '../../../hooks/useAxiosFetch';
import defaultData from '../../../data/defaultData.json'
const IndustriesList = () => {

    const getElement = (industry, index) => {
        if (index % 2 === 0) {
            return (
                <section className={industry.className}>
                    <div className="row flex-column-reverse flex-lg-row">
                        <div className="col-lg-6">
                            <div className="pictures">
                                <img
                                    src={industry.imgURL}
                                    alt={industry.alt}
                                />
                            </div>
                        </div>
                        <div className="col-lg-6">
                            <div className="description">
                                <div className="bulb"></div>
                                <p>
                                    our <span>industries</span>
                                </p>
                                <h2>{industry.name}</h2>
                                <p>{industry.paragraph}</p>
                                <a href="/#" type="button" className="btn rounded-0">
                                    LEARN MORE
                                </a>
                            </div>
                        </div>
                    </div>
                </section>
            )
        }
        return (
            <section className="Fintech">
                <div className="row left-to-right">
                    <div className="col-lg-6">
                        <div className="description">
                            <div className="bulb"></div>
                            <p>
                                our <span>industries</span>
                            </p>
                            <h2>{industry.name}</h2>
                            <p>
                                Lorem ipsum dolor sit amet, consectetur adpiscing elit, sed do
                                eius tempor incididunt ut labore et dolore magna aliqua. Nisl
                                tincidunt eget nullam non. Quis hendrerit dolor magna eget.
                            </p>
                            <a href="/#" type="button" className="btn rounded-0">
                                LEARN MORE
                            </a>
                        </div>
                    </div>
                    <div className="col-lg-6">
                        <div className="pictures">
                            <img src="images/Industries/fintech.png" alt="Fintech" />
                        </div>
                    </div>
                </div>
            </section>
        )
    }

    const [industryList, setIndustryList] = useState([]);

    const { data, fetchError, isLoading } = useAxiosFetch('http://localhost:3500/industries');

    useEffect(() => {
        if (isLoading) return;
        if (!isLoading && fetchError != null) {
            alert(fetchError + '!! Given Default');
            setIndustryList(prevIndustryList => defaultData.industries.map((industry, index) => { return getElement(industry, index) }));
            return;
        }
        setIndustryList(prevIndustryList => data.map((industry, index) => { return getElement(industry, index) }));

    }, [fetchError, data, isLoading]);
    return (
        <article id="ourIndustries" className={styles.ourIndustries}>
            <div className="container">
                {industryList}
            </div>
        </article>
    )
}

export default IndustriesList;
