import React, { useEffect, useState } from 'react'
import styles from '../../assets/cssModules/whychooseus.module.css'
import useAxiosFetch from '../../hooks/useAxiosFetch';
import defaultData from '../../data/defaultData.json'
import Bulb from '../../assets/images/bulb.png'


const Projects = () => {

    const getElement = (project, index) => {
        return (
            <div className="col-md-6 col-lg-4 mb-4">
                <div className={styles.projectItem}>
                    <img
                        src={project.imgURL}
                        alt="PJ1"
                        className="img-fluid"
                    />
                    <div className="content">
                        <img src="images/WhyChooseUs/bulb.png" alt="Bulb" />
                        <h6 className={styles.h6}>ABCXYZ</h6>
                        <p>
                            Lorem Ipsum is simply dummy text of the printing and typesetting
                            industry
                        </p>
                    </div>
                </div>
            </div>
        )
    }
    const [projectList, setProjectList] = useState([]);

    const { data, fetchError, isLoading } = useAxiosFetch('http://localhost:3500/projects');

    useEffect(() => {
        if (isLoading) return;
        if (!isLoading && fetchError != null) {
            alert(fetchError + ' in Projects !! Given Default');
            setProjectList(prevProjectsList => defaultData.services.map((project, index) => { return getElement(project, index) }));
            return;
        }
        setProjectList(prevProjectsList => (data.map((project, index) => { return getElement(project, index) })));

    }, [fetchError, data, isLoading]);

    return (
        <section id="projects" className={`${styles.Projects} ${styles.section}`}>
            <div className="container">
                <h2 className={styles.h2}>PROJECTS</h2>
                <div className={`row ${styles.row}`} >
                    {projectList}
                </div>
            </div>
        </section>
    )
}

export default Projects
