import React, { useContext, useEffect } from 'react'
import styles from '../../assets/cssModules/contact.module.css'
import FormContext from '../../context/FormContext'

const ContactForm = () => {

    const { handleChange, handleSubmit, values, errors, isSubmitting, setSuccess } = useContext(FormContext);

    useEffect(() => {
        if(Object.keys(errors).length === 0 && isSubmitting) {
            setSuccess(true);
        }
    }, [errors, isSubmitting, setSuccess]);
    useEffect({})
    return (
        <div className="col-md-6">
            <div className={styles.rightSite}>
                <div className="row">
                    <input
                        type="text"
                        id="name"
                        name="name"
                        placeholder="Name"
                        value={values.name}
                        onChange={handleChange}
                    />
                    {errors.name && <span className="error">{errors.name}</span>}
                </div>
                <div className="row">
                    <input
                        type="email"
                        id="email"
                        name="email"
                        placeholder="Email"
                        value={values.email}
                        onChange={handleChange}
                    />
                    {errors.email && <span className="error">{errors.email}</span>}
                </div>
                <div className="row">
                    <input
                        type="tel"
                        id="phone"
                        name="phone"
                        placeholder="Phone"
                        value={values.phone}
                        onChange={handleChange}
                    />
                    {errors.phone && <span className="error">{errors.phone}</span>}
                </div>
                <div className="row">
                    <input
                        type="text"
                        id="company"
                        name="company"
                        placeholder="Company"
                        value={values.company}
                        onChange={handleChange}
                    />
                    {errors.company && <span className="error">{errors.company}</span>}
                </div>
                <div className="row">
                    <textarea
                        name="message"
                        id="message"
                        cols={1}
                        rows={5}
                        placeholder="Message"
                        value={values.message}
                        onChange={handleChange}
                    />
                    {errors.message && <span className="error">{errors.message}</span>}
                </div>
                <div className="row">
                    <button type="submit" className="btn btn-primary rounded-0" onClick={handleSubmit}>
                        SEND
                    </button>
                </div>
            </div>
        </div>
    )
}

export default ContactForm
