import React, { useEffect, useState } from 'react'
import styles from '../../assets/cssModules/whychooseus.module.css'
import useAxiosFetch from '../../hooks/useAxiosFetch';
import defaultData from '../../data/defaultData.json'

const Benefits = () => {
    const getElement = (benefit, index) => {
        let className = '';
        if (index % 3 === 0) className = `col-md-6 col-lg-4 pr-lg-5 pr-xl-5 mb-5 ${styles.benefit}`;
        if (index % 3 === 1) className = `col-md-6 col-lg-4 px-lg-4 px-xl-4 mb-5 ${styles.benefit}`;
        if (index % 3 === 2) className = `col-md-6 col-lg-4 pl-lg-5 pl-xl-5 mb-5 ${styles.benefit}`;
        return (
            <div className={className}>
                <div className={styles.benefitItem}>
                    <div className="row">
                        <div className="col-md-2 pr-sm-1 pr-md-0 pr-lg-0 pr-xl-0 mb-3">
                            <div className={styles.benefitPic}>
                                <img
                                    src={benefit.imgURL}
                                    alt="benefit-icon"
                                    className="img-fluid"
                                />
                            </div>
                        </div>
                        <div className="col-md-10">
                            <h5 className={styles.h5}>{benefit.name}</h5>
                            <div className={styles.rec}></div>
                            <p>{benefit.paragraph}</p>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
    const [benefitList, setBenefitList] = useState([]);

    const { data, fetchError, isLoading } = useAxiosFetch('http://localhost:3500/benefits');

    useEffect(() => {
        if (isLoading) return;
        if (!isLoading && fetchError != null) {
            alert(fetchError + ' in Benefits !! Given Default');
            setBenefitList(prevBenefitsList => defaultData.services.map((benefit, index) => { return getElement(benefit, index) }));
            return;
        }
        setBenefitList(prevBenefitsList => (data.map((benefit, index) => { return getElement(benefit, index) })));

    }, [fetchError, data, isLoading]);

    return (
        <section id="benefit" className={`${styles.section} ${styles.benefit}`}>
            <div className={`container ${styles.noPadContainer}`}>
                <div className="row">
                    {benefitList}
                </div>
            </div>
        </section>
    )
}

export default Benefits
