import React from 'react'
import '../../assets/css/slick.css'
import Slider from "react-slick";
import defaultData from '../../data/defaultData'
import useData from '../../hooks/useData';
import { Link } from 'react-router-dom';
const SlideShow = () => {

    const getElement = (item) => {
        return (
            <div className="media-pic" key={item.id}>
                    <img
                        src={item.imgURL}
                        alt=""
                        className="img-fluid"
                    />
            </div>
        )
    }
    const path = 'http://localhost:3500/slideShow';
    const { list } = useData(path, defaultData.SlideShow, 'Slide Show', getElement);

    const settings = {
        dots: true,
        infinite: true,
        autoplaySpeed: 2000,
        speed: 1000,
        autoplay: true,
        slidesToShow: 3,
        slidesToScroll: 1,
        pauseOnHover: false,
        initialSlide: 0,
        swipeToSlide: true,
        responsive: [
            {
                breakpoint: 1000,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 1,
                }
            },
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1,
                }
            }
        ]
    };
    return (
        <Slider {...settings}>
            {list}
        </Slider>
    );
}

export default SlideShow;
