import React, { useEffect, useState } from 'react'
import styles from '../../assets/cssModules/homepage.module.css';
import useAxiosFetch from '../../hooks/useAxiosFetch';
import defaultData from '../../data/defaultData.json'

const Services = () => {

    const getElement = (service, index) => {
        let className = '';
        if (index % 3 === 0) className = 'col-sm-6 col-md-4 pr-lg-5 pr-xl-5 mb-5 service';
        if (index % 3 === 1) className = 'col-sm-6 col-md-4 px-lg-4 px-xl-4 mb-5 service';
        if (index % 3 === 2) className = 'col-sm-6 col-md-4 pl-lg-5 pl-xl-5 mb-5 service';
        return (
            <div className={className} key={service.id}>
                <div className="pic">
                    <img
                        src={service.imgURL}
                        alt="sv"
                        className="img-fluid"
                    />
                </div>
                <h4 className={styles.h4}>{service.svName}</h4>
                <p>{service.content}</p>
            </div>
        )
    }
    const [serviceList, setServiceList] = useState([]);

    const { data, fetchError, isLoading } = useAxiosFetch('http://localhost:3500/services');

    console.log((serviceList));
    useEffect(() => {
        if (isLoading) return;
        if (!isLoading && fetchError != null) {
            alert(fetchError + ' in Services Section !! Given Default');
            setServiceList(prevServiceList => defaultData.services.map((service, index) => { return getElement(service, index) }));
            return;
        }
        setServiceList(prevServiceList => (data.map((service, index) => { return getElement(service, index) })));

    }, [fetchError, data, isLoading]);

    return (
        <section id="services" className={styles.services}>
            <div className={`container ${styles.noPadContainer}`}>
                <div className="row">
                    <div className="col-12">
                        <h2 className={styles.h2}>OUR SERVICES</h2>
                    </div>
                    {serviceList}
                </div>
            </div>
        </section>
    )
}

export default Services
