import { useContext, useEffect } from 'react';
import HeaderContext from '../context/PageContext';

const useHeader = (path) => {

    const { setPath } = useContext(HeaderContext);
    useEffect(() => {
        setPath(path);
    }, [setPath, path]);

};

export default useHeader;
