import React, { useContext } from 'react'
import styles from '../../assets/cssModules/contact.module.css'
import FormContext from '../../context/FormContext'

const ContactForm = () => {

    const { handleChange, handleSubmit, values, errors, isSubmitting } = useContext(FormContext);

    return (
        <div className="col-md-6">
            <div className={styles.rightSite}>
                <div className="row">
                    <input
                        className={`${isSubmitting === true && errors.name && "inputError"}`}
                        type="text"
                        name="name"
                        placeholder="Name"
                        value={values.name}
                        onChange={handleChange}
                        autoFocus={false}
                    />
                    {isSubmitting === true && errors.name && <span className="error">{errors.name}</span>}
                </div>
                <div className="row">
                    <input
                        className={`${errors.email && "inputError"}`}
                        type="email"
                        name="email"
                        placeholder="Email"
                        value={values.email}
                        onChange={handleChange}
                    />
                    {errors.email && <span className="error">{errors.email}</span>}
                </div>
                <div className="row">
                    <input
                        className={`${errors.phone && "inputError"}`}
                        type="tel"
                        name="phone"
                        placeholder="Phone"
                        value={values.phone}
                        onChange={handleChange}
                    />
                    {errors.phone && <span className="error">{errors.phone}</span>}
                </div>
                <div className="row">
                    <input
                        className={`${errors.company && "inputError"}`}
                        type="text"
                        name="company"
                        placeholder="Company"
                        value={values.company}
                        onChange={handleChange}
                    />
                    {errors.company && <span className="error">{errors.company}</span>}
                </div>
                <div className="row">
                    <textarea
                        className={`${errors.message && "inputError"}`}
                        name="message"
                        cols={1}
                        rows={5}
                        placeholder="Message"
                        value={values.message}
                        onChange={handleChange}
                    />
                    {errors.message && <span className="error">{errors.message}</span>}
                </div>
                <div className="row">
                    <button type="submit" className="btn btn-primary rounded-0" onClick={handleSubmit}>
                        SEND
                    </button>
                </div>
            </div>
        </div>
    )
}

export default ContactForm;
