import React, { useEffect, useState } from 'react'
import styles from '../../assets/cssModules/whychooseus.module.css'
import useAxiosFetch from '../../hooks/useAxiosFetch';
import defaultData from '../../data/defaultData.json'
const Reasons = () => {
    const getElement = (reason, index) => {
        let className = '';
        if (index % 3 === 0) className = `col-sm-6 col-md-4 pr-lg-5 pr-xl-5 mb-3 ${styles.reason}`;
        if (index % 3 === 1) className = `col-sm-6 col-md-4 px-lg-4 px-xl-4 mb-3 ${styles.reason}`;
        if (index % 3 === 2) className = `col-sm-6 col-md-4 pl-lg-5 pl-xl-5 mb-3 ${styles.reason}`;
        return (
            <div className={className} key={reason.id}>
                <div className={styles.pic}>
                    <img
                        src={reason.imgURL}
                        alt="reason-icon"
                        className="img-fluid"
                    />
                </div>
                <h4 className={styles.h4}>{reason.name}</h4>
                <p>{reason.paragraph}</p>
            </div>
        )
    }
    const [reasonList, setReasonList] = useState([]);

    const { data, fetchError, isLoading } = useAxiosFetch('http://localhost:3500/reasons');

    useEffect(() => {
        if (isLoading) return;
        if (!isLoading && fetchError != null) {
            alert(fetchError + ' in Reasons !! Given Default');
            setReasonList(prevReasonsList => defaultData.services.map((reason, index) => { return getElement(reason, index) }));
            return;
        }
        setReasonList(prevReasonsList => (data.map((reason, index) => { return getElement(reason, index) })));

    }, [fetchError, data, isLoading]);

    return (
        <section id="reasons" className={`reasons ${styles.section}`}>
            <div className={`container ${styles.noPadContainer}`}>
                <div className="row">
                    <div className="col-12">
                        <h2 className={styles.h2}>REASONS YOU SHOULD BE CONSIDERING BLOCKCHAIN</h2>
                    </div>
                    {reasonList}
                </div>
            </div>
        </section>
    )
}

export default Reasons;
