import React from 'react'
import Slider from "react-slick";

const Caro = () => {

    const settings = {
        dots: true,
        infinite: true,
        speed: 2000,
        autoplaySpeed: 5000,
        autoplay: true,
        slidesToShow: 3,
        slidesToScroll: 2,
        pauseOnHover: false,
        initialSlide: 0,
        customPaging: i => (
            <div 
            style={{
                width: "5px",
                height: "1px",
            }}>
                {i}
            </div>
        ),
        responsive: [
            {
                breakpoint: 1000,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 2,
                }
            },
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1,
                }
            }
        ]
    };
    return (
        <Slider {...settings}>
            {slideData.map(item => (
                <div className="media-pic" key={item.id}>
                    <a href="/#">
                        <img
                            src={item.imgURL}
                            alt=""
                            className="img-fluid"
                        />
                    </a>
                </div>
            ))}
        </Slider>
    );
}

