import React from 'react'
import { useState, useEffect } from 'react'
import '../../assets/css/carousel.css'
import '../../assets/css/owl.carousel.min.css'
import defaultData from '../../data/defaultData.json'
import useAxiosFetch from '../../hooks/useAxiosFetch'

const SlideShow = () => {
    const getElement = (slide) => {
        return (
            <div className="media-pic" key={slide.id}>
                <a href="/#">
                    <img
                        src={slide.imgURL}
                        alt="slide"
                        className="img-fluid"
                    />
                </a>
            </div>
        )
    }
    const [slideShow, setSlideShow] = useState(defaultData.slideshow.map(slide => { return getElement(slide) }));

    const { data, fetchError, isLoading } = useAxiosFetch('http://localhost:3500/slideshow');

    console.log(defaultData.slideshow);
    useEffect(() => {
        if (isLoading) return;
        if (!isLoading && fetchError != null) {
            alert(fetchError + ' in Slide Show!! Given Default');
            return;
        }

        // setSlideShow(prevSlideShow => (data.map(slide => {return getElement(slide)})));
    }, [fetchError, data, isLoading]);
    
    return (
        <div className="slide-show px-4">
            <div className="contairner-fluid mt-5">
                <div className="site-section mb-5">
                    <div className="owl-2-style">
                        <div className="owl-carousel owl-2">
                            {slideShow}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default SlideShow;
