import React, { useEffect, useState } from 'react'
import styles from '../../assets/cssModules/ourteam.module.css'
import In_icon from '../../assets/images/in-icon.png'
import Twitter_icon from '../../assets/images/twitter-icon.png'
import useAxiosFetch from '../../hooks/useAxiosFetch';
import defaultData from '../../data/defaultData.json'

const Leaders = () => {

    const getElement = (leader) => {
        return (
            <div className="col-xs-12 col-sm-6 col-md-4 col-lg-3 mb-4" key={leader.id}>
                <div className="profile-card shad">
                    <div className="card border-secondary rounded-0">
                        <div className="card-body">
                            <div className="user-box">
                                <img src={leader.imgURL}  alt="profile-img" />
                            </div>
                            <h4 className="mb-1">{leader.name}</h4>
                            <h6>{leader.position}</h6>
                            <p>
                                <a href="/#">
                                    <img src={In_icon} alt="In-icon" />
                                </a>
                                <a href="/#">
                                    <img
                                        src={Twitter_icon}
                                        alt="Twitter-icon"
                                    />
                                </a>
                            </p>
                        </div>
                    </div>
                </div>
            </div>
        )
    }

    const [leaderTeam, setLeaderTeam] = useState([]);

    const { data, fetchError, isLoading } = useAxiosFetch('https://localhost:3500/leaders');

    useEffect(() => {
        if (isLoading) return;
        if (!isLoading && fetchError != null) {
            alert(fetchError + ' in Leader Team !! Given Default');
            setLeaderTeam(prevLeaderTeam => defaultData.leaders.map((leader, index) => { return getElement(leader, index) }));
            return;
        }
        setLeaderTeam(prevLeaderTeam => (data.map((leader, index) => { return getElement(leader, index) })));

    }, [fetchError, data, isLoading]);

    return (
        <section id="leader-team" className={styles.leaderTeam}>
            <div className="container text-center">
                <h2>LEADER TEAM</h2>
                <h4>
                    Our team is up of product, business and technology experts who are
                    passionate about the future of blockchain technology.
                </h4>
                <div className="row">
                    {leaderTeam}
                </div>
            </div>
        </section>
    )
}

export default Leaders
