import React, { useEffect, useState } from 'react'
import Navbar from './Navbar';
import styles from '../assets/cssModules/header.module.css'
import HomePage from '../pages/HomePage';

const Header = (props) => {

    const [element, setElement] = useState(<header>Header not found</header>);

    const space = ' ';
    const h1_HomePage_class = 'd-none d-sm-block' 
    const a_HomePage = 

    useEffect(() => {
        const Page = props.Page;
        switch (Page) {
            case 'whychooseus':
            case 'industries':
            case 'works':
            case 'contact':
                setElement((prevElement) => 
                    <header>
                        <div className="container">
                            {/* NAVBAR*/}
                            <Navbar navTheme={'White'} />
                            {/*/.END NAVBAR*/}
                        </div>
                    </header>
                )
                break;
            case 'homepage':
                setElement((prevElement) => 
                    <header className={styles.homePageHeader}>
                        <div className="container h-100 d-flex flex-column">
                            {/* NAVBAR*/}
                            <Navbar navTheme={'Trans'} />
                            {/*/.END NAVBAR*/}
                            <div className="row flex-fill align-items-center content">
                                <div className="col-md-5 p-0">
                                    <h1 className={"d-none d-sm-block" + ' ' + styles.h1-HomePage}>
                                        Develop High-tech products for Enterprise
                                    </h1>
                                    <p>
                                        We provide excellent development services in a wide range off business
                                        industries with distributed systems based on Blockchain technology
                                    </p>
                                    <a href="/#" className="btn rounded-0">
                                        LEARN MORE
                                    </a>
                                </div>
                            </div>

                        </div>
                    </header>
                )
                break;
            case 'ourteam':
                setElement((prevElement) => 
                    <header className='ourTeamHeader'>
                        <div className="container">
                            {/* NAVBAR*/}
                            <Navbar navTheme={'Trans'} />
                            {/*/.END NAVBAR*/}
                            <div className="row">
                                <div className="col-md-5">
                                    <div className="content">
                                        <h1>Our Vision</h1>
                                        <p>We strive to help change the world with Blockchain Technology</p>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </header>
                )
                break;
            default:
        }

    }, [props])

    return (
        <>
            {element}
        </>
    )
}

export default Header;
