import React, { useEffect, useState } from 'react'
import styles from '../../../assets/cssModules/homepage.module.css';
import useAxiosFetch from '../../../hooks/useAxiosFetch';
import defaultData from '../../../data/defaultData.json'
import Bulb from '../../../assets/images/bulb.png'

const Partners = () => {
    const getElement = (partner, index) => {
        let classN = '';
        if (index % 3 === 0) classN = `${styles.content} ${styles.partner_1}`;
        if (index % 3 === 1) classN = `${styles.content} ${styles.partner_2}`;
        if (index % 3 === 2) classN = `${styles.content} ${styles.partner_3}`;
        return (
            <div className="col-md-6 col-lg-4 mb-5" key={partner.id}>
                <div className={classN}>
                    <img src={Bulb} alt="Bulb" />
                    <div className={`text text-center ${styles.txt}`}>
                        <h6 className={`text-left pl-5 ${styles.h6}`}>{partner.partnerName}</h6>
                        <p>{partner.partnerContext}</p>
                        <a href="/#" className="btn rounded-0">
                            Learn more
                        </a>
                    </div>
                </div>
            </div>
        )
    }

    const [numItem, setNumItem] = useState(3);
    const [partnerList, setPartnerList] = useState(defaultData.partners.slice(0, 3).map((partner, index) => { return getElement(partner, index) }));

    const { data, fetchError, isLoading } = useAxiosFetch('http://localhost:3500/partners');

    useEffect(() => {
        if (isLoading) return;
        if (!isLoading && fetchError != null) {
            alert(fetchError + ' in Services Section !! Given Default');
            return;
        }
        setPartnerList(prevPartnerList => (data.splice(0, numItem).map((partner, index) => { return getElement(partner, index) })));

    }, [fetchError, data, isLoading]);

    return (
        <section id="partners" className={styles.partners}>
            <div className="container">
                <h2 className={styles.h2}>OUR PARTNER</h2>
                <div className={`row ${styles.partner} ${styles.height}`}>
                    {partnerList}
                </div>
                <div>
                    <a href="/#" className={`btn btn-default extend-btn rounded-0 ${styles.extendButton}`} onClick={handleClick}>
                        VIEW MORE
                    </a>
                </div>
            </div>
        </section>
    )
}

export default Partners;
