import {
    CREATE_ITEM,
    RETRIEVE_ITEMS,
    UPDATE_ITEM,
    DELETE_ITEM,
    DELETE_ALL_ITEMS,
  } from "../actions/types";
  
  const initialState = [];
  
  const ITEMReducer = (ITEMs = initialState, action) => {
    const { type, payload } = action;
  
    switch (type) {
      case CREATE_ITEM:
        return [...ITEMs, payload];
  
      case RETRIEVE_ITEMS:
        return payload;
  
      case UPDATE_ITEM:
        return ITEMs.map((ITEM) => {
          if (ITEM.id === payload.id) {
            return {
              ...ITEM,
              ...payload,
            };
          } else {
            return ITEM;
          }
        });
  
      case DELETE_ITEM:
        return ITEMs.filter(({ id }) => id !== payload.id);
  
      case DELETE_ALL_ITEMS:
        return [];
  
      default:
        return ITEMs;
    }
  };
  
  export default ITEMReducer;
