import React from 'react'
import Slider from "react-slick";

const Caro = () => {
    const settings = {
        dots: true,
        infinite: false,
        speed: 500,
        autoplaySpeed: 2000,
        autoplay: true,
        slidesToShow: 3,
        slidesToScroll: 3,
        initialSlide: 0,
        margin: 20,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 3,
                    slidesToScroll: 3,
                    infinite: true,
                    dots: true
                }
            },
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 2,
                    initialSlide: 2
                }
            },
            {
                breakpoint: 480,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1
                }
            }
        ]
    };
    return (
        <Slider {...settings}>
            {slideData.map(item => (
                <div className='card'>
                    <div className='cardTitle'>
                        <h1>{item.fname}</h1>
                        <h2>{item.lname}</h2>
                    </div>
                    <div className='cardContent'>
                        <p>{item.age}</p>
                    </div>
                </div>
            ))}
        </Slider>
    );
}

const slideData = [
    {
        fname: 'Jack',
        lname: 'Harrison',
        age: 19
    },
    {
        fname: 'Jack',
        lname: 'Harrison',
        age: 19
    },
    {
        fname: 'Jack',
        lname: 'Harrison',
        age: 19
    },
    {
        fname: 'Jack',
        lname: 'Harrison',
        age: 19
    }, {
        fname: 'Jack',
        lname: 'Harrison',
        age: 19
    },
    {
        fname: 'Jack',
        lname: 'Harrison',
        age: 19
    }
]

export default Caro;
