import React, { useEffect, useState } from 'react'
import styles from '../../assets/cssModules/works.module.css'
import useAxiosFetch from '../../hooks/useAxiosFetch';
import defaultData from '../../data/defaultData.json'

const Recents = () => {
    const getElement = (recent) => {
        return (
            <div className="col-12 col-sm-6 col-md-4 mb-5">
                <div className={styles.recentPic}>
                    <img
                        src={recent.imgURL}
                        alt="Projects"
                        className="img-fluid"
                    />
                </div>
                <span>{recent.des}</span>
                <p>{recent.paragraph}</p>
                <span className="link">
                    <a href="/#">Read More</a>
                </span>
            </div>
        )
    }

    const [recentList, setRecentList] = useState([]);
    const [numItem, setNumItem] = useState(6);
    const [hidden, setHidden] = useState(false);
    const { data, fetchError, isLoading } = useAxiosFetch('http://localhost:3500/recents');

    const handleClick = (e) => {
        e.preventDefault();
        if (numItem + 3 >= data.length) {
            setHidden(true);
        }
        setNumItem(prevNumItem => Math.min(prevNumItem + 3, parseInt(data.length)));
    }
    useEffect(() => {
        if (isLoading) return;
        if (!isLoading && fetchError != null) {
            alert(fetchError + ' in Recents !! Given Default');
            setRecentList(prevRecentsList => defaultData.recents.map(recent => { return getElement(recent) }));
            return;
        }
        setRecentList(prevRecentsList => (data.map(recent => { return getElement(recent) })));

    }, [fetchError, data, isLoading]);

    return (
        <section id="recent-projects" className={styles.recents}>
            <div className="container">
                <h2>RECENT PROJECTS</h2>
                <div className={`row ${styles.recentItem}`}>
                    {recentList}
                </div>
                <div className="d-flex justify-content-center">
                    <button
                        type="button"
                        className={`btn rounded-0 d-flex justify-content-center ${styles.load}`}
                        onClick = {handleClick}
                    >
                        LOAD MORE
                    </button>
                </div>
            </div>
        </section>

    )
}

export default Recents;
