import React, { useEffect, useState } from 'react'
import Navbar from './Navbar';
import styles from '../assets/cssModules/header.module.css'

const Header = () => {

    const [element, setElement] = useState(<header>Header not found</header>);

    useEffect(() => {
        switch (window.location.pathname) {
            case '/whychooseus':
            case '/industries':
            case '/works':
            case '/contact':
                setElement((prevElement) =>
                    <header>
                        <div className="container">
                            {/* NAVBAR*/}
                            <Navbar />
                            {/*/.END NAVBAR*/}
                        </div>
                    </header>
                )
                break;
            case '/':
                setElement((prevElement) =>
                    <header className={styles.header_HomePage}>
                        <div className="container h-100 d-flex flex-column">
                            {/* NAVBAR*/}
                            <Navbar />
                            {/*/.END NAVBAR*/}
                            <div className="row flex-fill align-items-center">
                                <div className="col-md-5 p-0">
                                    <h1 className={`d-none d-sm-block ${styles.h1_HomePage}`}>
                                        Develop High-tech products for Enterprise
                                    </h1>
                                    <p className={styles.p_HomePage}>
                                        We provide excellent development services in a wide range off business
                                        industries with distributed systems based on Blockchain technology
                                    </p>
                                    <a href="/#" className={`btn rounded-0 ${styles.a_HomePage}`}>
                                        LEARN MORE
                                    </a>
                                </div>
                            </div>

                        </div>
                    </header>
                )
                break;
            case 'ourteam':
                setElement((prevElement) =>
                    <header className={styles.header_OurTeam}>
                        <div className="container">
                            {/* NAVBAR*/}
                            <Navbar />
                            {/*/.END NAVBAR*/}
                            <div className="row">
                                <div className="col-md-5">
                                    <div className={styles.content}>
                                        <h1 className={styles.h1_OurTeam}>Our Vision</h1>
                                        <p className={styles.p_OurTeam}>We strive to help change the world with Blockchain Technology</p>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </header>
                )
                break;
            default:
        }

    }, []);

    return (
        <>
            <header className={
                (window.location.pathname === '/' && styles.header_HomePage) || 
                (window.location.pathname === '/' && styles.header_OurTeam)
            }>
                <div className={`container ${window.location.pathname === '/' && 'h-100 d-flex flex-column'}`}>
                    {/* NAVBAR*/}
                    <Navbar />
                    {/*/.END NAVBAR*/}
                </div>
            </header>
        </>
    )
}

export default Header;
