import $ from 'jquerry'
const slide = () => {
    $(function() {
        if ( $('.owl-2').length > 0 ) {
        console.log("Hello");
            $('.owl-2').owlCarousel({
                center: false,
                items: 1,
                loop: true,
                stagePadding: 0,
                margin: 20,
                smartSpeed: 1000,
                autoplay: true,
                nav: true,
                dots: true,
                pauseOnHover: false,
                responsive:{
                    600:{
                        margin: 20,
                        nav: true,
                      items: 2
                    },
                    1000:{
                        margin: 20,
                        stagePadding: 0,
                        nav: true,
                      items: 3
                    }
                }
            });            
        }
    });
}

export default slide;
