import { useContext, useEffect } from 'react';
import HeaderContext from '../context/HeaderContext';

const useHeader = () => {

    const { setPath } = useContext(HeaderContext);
    useEffect(() => {
        setPath(window.location.pathname);
    }, [setPath]);

};

export default useHeader;
