import React, { useEffect, useState } from 'react'
import styles from '../../assets/cssModules/ourteam.module.css'
import defaultData from '../../data/defaultData.json'


const TopReasons = () => {

    const getElement = (Item) => {
        return (
            <div className={`col-md-4 ${styles.reason}`} key={Item.id}>
                <div className={styles.pic}>
                    <img
                        src={Item.imgURL}
                        alt="Top-icon"
                        className="img-fluid"
                    />
                </div>
                <p>{Item.paragraph}</p>
            </div>
        )
    }
    const [listItem, setListItem] = useState([]);

    useEffect(() => {
        setListItem(preListItem => (defaultData.topReasons.map(Item => {getElement(Item)})));
    });
    return (
        <section id="top-reasons" className={styles.topReasons}>
            <div className={`container ${styles.noPadContainer}`}>
                <div className="row">
                    {listItem}
                </div>
            </div>
        </section>
    )
}

export default TopReasons
